import React, { useEffect, useState } from 'react';
import { db } from '../firebase';
import { StyleSheet, css } from 'aphrodite';
import Card from '../components/card';

function SeeOrder() {
    const [pendent, setPendent] = useState([]);

    useEffect(() => {
        db.collection('Pedidos')
            .onSnapshot((snapshot) => {
                const getOrder = snapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data()
                }))
                setPendent(getOrder);
            })
    });

    return (
        <main>
            <div className={css(styles.card)}>
                {pendent.map((order) =>
                    <Card key={order.id}>
                        <section >
                            <div>
                                {order.clientName}
                                {order.table}
                                {order.order.map(item => (
                                    <p>{item.name}</p>
                                ))}
                                {order.status}
                                {order.time}
                            </div>
                        </section>
                    </Card>
                )}
            </div>


        </main>
    )
}

 const styles = StyleSheet.create({
    card: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around',
    flexDirection: 'row',
    width: '25%',
    height: '15%',
    borderRadius: '10px',
    border: '#c5350ddc',
    marginTop:'15%',
    position: 'absolute',
    top: '20px',
    left: '10px',
    padding: '5% 5% 10% 5%',

    }
}); 

export default SeeOrder;
