import React, { useEffect, useState } from 'react';
import { db } from './firebase.js';
import Card from './components/card';

function SeeOrder() {

    const [pendent, setPendent] = useState([]);


    useEffect(() => {
        db.collection('Pedidos')
            .onSnapshot((snapshot) => {
                const getOrder = snapshot.docs.map((doc) => ({
                    id: doc.id,
                    ...doc.data()
                }))
                setPendent(getOrder);
            })
        });

        console.log(pendent);


    return (
        <main>
            {pendent.map((order) =>
            console.log(order)
