import React, { useState } from 'react';
/* import logo from './logo.svg'; */
import './App.css';
import { db } from './firebase.js';

function App() {
  const [dados, setDados] = useState([]);
  db.collection('Menu')
    .get()
    .then((snapshot) => {
      const banana = snapshot.docs.map((doc) => ({
        ...doc.data()
      }))
      /* (console.log(doc.data())) */
      setDados(banana)
    });

  return (
    <>
      <div>{dados.map((item) =>
        <div>
          {item.name}
          <br></br>
          R${item.price}
        </div>
      )}
      </div>
    </>



