import React, { useState, useEffect } from 'react';
import { db } from '../../firebase.js';
import Card from '../card';
import Button from '../button';
import Input from '../input';
import './Cliente.css';

function FazerPedido() {
  const [dados, setDados] = useState([]);
  const [item, setItem] = useState([]);
  const [table, setTable] = useState('');
  const [cliente, setCliente] = useState('');

  useEffect(() => {
    db.collection('Menu')
      .get()
      .then((snapshot) => {
        const banana = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data()
        }))
        /* (console.log(doc.data())) */
        setDados(banana)
      });
  }, [0])


  const filterMenu = (event) => {
    const idItem = event.target.id;
    const showMenu = (idItem === 'breakfast') ? true : false;
    const filterItens = dados.filter((itensMenu) => itensMenu.breakfast === showMenu)
    return setItem(filterItens)
  }
  console.log(filterItens)

  return (
    <>
      <main>
        <div key={'banana'} className='btn'>
          <Button
            id='breakfast'
            onClick={filterMenu}
            children='ManhÃ£'
          />
          <Button
            id='btn'
            onClick={filterMenu}
            children='Tarde'
          />
        </div>
      </main>
      <article >
        <div key={dados.id} className='card-container'>
          {item.map((item) =>
            <Card
              key={item.id}
              onClick={() => console.log(item)}>
              <div className='card'>
                {item.name}
              </div>
              <div className='card'>
                R${item.price}
              </div>
            </Card>
          )}
        </div>
      </article>
      <aside className='aside'>
        <form className='container-aside'>
          <div className='input'>
            <Input
              placeholder='Cliente'
              onChange={(event) =>
                setCliente(event.target.value)} />
            <Input
              placeholder='Mesa'
              onChange={(event) =>
                setTable(event.target.value)} />
            <p className='p'>{cliente}</p>
            <p className='p'>{table}</p>
            <Input 
          type='submit'/>
          </div>

        </form>
      </aside>
    </>
  )
}
export default FazerPedido;
