import React, { useState, useEffect } from 'react';
import { db } from '../../firebase.js';
import firebase from 'firebase/app';
import Card from '../card';
import Button from '../button';
import Input from '../input';
import './Cliente.css';

function TakeOrder() {
  const [dados, setDados] = useState([]);
  const [item, setItem] = useState([]);
  const [table, setTable] = useState('');
  const [cliente, setCliente] = useState('');
  const [order, setOrder] = useState([]);
  const [counter, setCounter] = useState(0);

  useEffect(() => {
    db.collection('Menu')
      .get()
      .then((snapshot) => {
        const getMenu = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data()
        }))
        /* (console.log(doc.data())) */
        setDados(getMenu)
      });
  }, [0])


  const filterMenu = (event) => {
    const idItem = event.target.id;
    const showMenu = (idItem === 'breakfast') ? true : false;
    const filterItens = dados.filter((itensMenu) => itensMenu.breakfast === showMenu)
    return setItem(filterItens)
  }
  /* console.log(item); */

  const showOrder = (item) => {
    setOrder([...order, item]);
    setCounter(counter + item.price);
  }

  const sendOrder = () => {
    db.collection('Pedidos')
      .add({
        clientName: cliente,
        table: table,
        order: order,
        /*      totalBill: totalBill,*/
        status: 'Pendente',
        time: firebase.firestore.FieldValue.serverTimestamp()
      })
      .then(() => {
        setOrder([]);
        setTable(['']);
        setCliente(['']);
        setCounter(0);
      })
    console.log('Enviado')
  }

  return (
    <>
      <main>
        <div key={'itensMenu'} className='btn'>
          <Button
            id='breakfast'
            onClick={filterMenu}
            children='ManhÃ£'
          />
          <Button
            id='btn'
            onClick={filterMenu}
            children='Tarde'
          />
        </div>
      </main>
      <article >
        <div key={dados.id} className='card-container'>
          {item.map((item) =>
            <Card
              key={item.id}
              onClick={() => showOrder(item)}>
              <div className='card'>
                {item.name}
              </div>
              <div className='card'>
                R${item.price}
              </div>
            </Card>
          )}
        </div>
      </article>
      <aside className='aside'>
        <form className='container-aside'>
          <div className='input'>
            <div >
          {order.map(item => <p key={item.id}>
              {item.name}
              R${item.price}
              <Button onClick={() => deleteItem(item) }></Button>
            </p>
              )}
             <p> Total: R$  {counter}  </p>
             </div>
            <Input
              placeholder='Cliente'
              onChange={(event) =>
                setCliente(event.target.value)} />
            <Input
              placeholder='Mesa'
              onChange={(event) =>
                setTable(event.target.value)} />
            <p className='p'>{cliente}</p>
            <p className='p'>{table}</p>
            <Input
              type='button' children='Enviar' onClick={sendOrder} />
          </div>  
        </form>
      </aside>
    </>
  )
}


export default TakeOrder;
